INSERT INTO pokemon (numero_identificador, nombre, tipo_id, descripcion, imagen_ruta) VALUES
                                                                                          (1, 'Bulbasaur', 5, 'Un Pokémon cuadrúpedo de color verde con manchas más oscuras. Tiene un bulbo en el lomo que crece con la edad.', 'public/img/1.png'),
                                                                                          (2, 'Ivysaur', 5, 'Cuando el bulbo de su lomo se hincha, desprende un aroma dulce y emite más energía.', 'public/img/2.png'),
                                                                                          (3, 'Venusaur', 5, 'Su planta desprende un aroma dulce que intensifica las emociones de otros Pokémon.', 'public/img/3.png'),
                                                                                          (4, 'Charmander', 2, 'La llama de su cola indica sus emociones. Arde con más fuerza cuando está emocionado.', 'public/img/4.png'),
                                                                                          (5, 'Charmeleon', 2, 'Cuando agita su ardiente cola, eleva gradualmente la temperatura a su alrededor.', 'public/img/5.png'),
                                                                                          (6, 'Charizard', 2, 'Escupe fuego que es tan caliente que puede derretir cualquier cosa.', 'public/img/6.png'),
