-- Crear base de datos Pokédex
CREATE DATABASE IF NOT EXISTS pokedex CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE pokedex;

-- Tabla de tipos de Pokémon
CREATE TABLE tipos (
                       id INT AUTO_INCREMENT PRIMARY KEY,
                       nombre VARCHAR(50) NOT NULL UNIQUE,
                       imagen_ruta_tipo VARCHAR(255)
);

-- Tabla principal de Pokémon
CREATE TABLE pokemon (
                         id INT AUTO_INCREMENT PRIMARY KEY,
                         numero_identificador INT NOT NULL UNIQUE,
                         nombre VARCHAR(100) NOT NULL,
                         tipo_id INT NOT NULL,
                         descripcion TEXT,
                         imagen_ruta VARCHAR(255),
                         FOREIGN KEY (tipo_id) REFERENCES tipos(id)
);




